import React,{useState} from "react";
import CardList from "./cardList";
// import SearchBox from "./SearchBox"
import { robots } from './robots';



const App = () => {
    const [search, setSearch] = useState("") 

    function searchFilter(data){
        return data.filter((item)=> item.name.toLowerCase().includes(search))
    }

    return (
        <div className="tc">
        <h1>RoboFriends</h1>
        <div className="pa2">
        <input type="search" placeholder="search robots" className="pa2 ba b-green bg-lightest-blue" onChange={(e)=> setSearch(e.target.value)}/>
        </div>
        {/* <SearchBox /> */}
        <CardList robots = { robots } filter = {searchFilter(robots)} />

        </div>
    )
}


export default App
